<script>
    $(function() {
        // middle bar close
        $('.middle-bar-close-button').on('click', function() {
            hideMiddleBar();
        });
        $(document).on('mouseup', function(e) 
        {
            var container = $(".middle-bar, .middle-bar-element");

            // if the target of the click isn't the container nor a descendant of the container
